mapper.select:
select count(u_id)
from user_info
where u_id = #{id}

mapper.update:
update user_info
set u_active_state = 1,
u_email_active_key = "0"
where u_email = #{u_email}
and u_email_active_key = #{u_email_active_key}

mapper.select:
select *
from user_info
where u_id = #{u_id}
and u_pwd = #{u_pwd}

mapper.update:
update user_info
set u_latest_login_date = CURRENT_TIMESTAMP
where u_id = #{u_id}
and u_pwd = #{u_pwd}

update user_info
set u_recommend_active_time = TIMESTAMPADD(minute, 5, now())
where u_id = #{u_id}

mapper.select:
select u_recommend_active_time
from user_info
where u_id = #{u_id}

mapper.delete:
	delete from user_info
   where u_id = #{u_id}
   and u_pwd = #{u_pwd}
